INSERT INTO `directions` (`id`, `nom`, `directeur`, `description`, `objectif`, `supprimer`) VALUES
(1, 'DESI', 13, '<p>Direction Etudes, Statistiques et Innovation</p>', '<ul>\r\n	<li>De mettre en place des outils informatiques efficaces et performants ;</li>\r\n	<li>D&rsquo; assurer et optimiser la s&eacute;curit&eacute; informatique des installations de l&rsquo;Agence ;</li>\r\n	<li>D&rsquo; aider &agrave; la digitalisation et &agrave; la centralisation des donn&eacute;es ;</li>\r\n	<li>De simplifier les t&acirc;ches &agrave; effectuer et am&eacute;liorer l&rsquo;acc&egrave;s &agrave; l&rsquo;information ;</li>\r\n	<li>De recueillir, analyser et exploiter les informations sur le march&eacute; des IDE pour la (es) zones (s) g&eacute;ographiques et/ou secteurs (s) d&rsquo;activit&eacute;s &agrave; savoir : les flux directs et indirects, l&rsquo;origine et les raisons des flux identifi&eacute;s, la concurrence et les motivations et attentes des investisseurs.</li>\r\n</ul>', 0),
(2, 'COM & MARKETING', 13, '<p>Service Communication &amp; Marketing</p>', '<ul>\r\n	<li>\r\n	<p>&nbsp;Promouvoir l&rsquo;image de l&rsquo;agence et du pays</p>\r\n	</li>\r\n	<li>\r\n	<p>Coordonner la production des outils et supports de communication</p>\r\n	</li>\r\n	<li>\r\n	<p>D&eacute;finir toutes les nouvelles activit&eacute;s de communication pouvant contribuer &agrave; l&rsquo;atteinte des objectifs de l&rsquo;agence</p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;Participer sur demande de la Direction G&eacute;n&eacute;rale &agrave; des manifestations externes auxquelles l&rsquo;APIP est impliqu&eacute;e (organisation mat&eacute;rielle et pr&eacute;sence physique)&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Promouvoir, d&eacute;velopper et adapter cette image aupr&egrave;s d&#39;un public vari&eacute; : investisseurs, partenaires, ambassadeurs, pouvoirs publics...&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Fournir des informations sur l&#39;agence, ses activit&eacute;s, son environnement et sa strat&eacute;gie par le biais de l&#39;organisation d&#39;&eacute;v&eacute;nements (visites, expositions, s&eacute;minaires, forums...)&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Nouer des contacts avec les m&eacute;dias afin de renforcer la visibilit&eacute; et la r&eacute;putation de l&rsquo;APIP</p>\r\n	</li>\r\n	<li>\r\n	<p>&nbsp;Collecter et diffuser aupr&egrave;s des guin&eacute;ens de l&rsquo;&eacute;tranger, les informations sur les opportunit&eacute;s d&rsquo;investissement et de financement de projets</p>\r\n	</li>\r\n	<li>\r\n	<p>&Eacute;laborer les rapports d&rsquo;activit&eacute;s p&eacute;riodiques ou annuels &agrave; transmettre &agrave; la direction sp&eacute;cifi&eacute;e</p>\r\n	</li>\r\n</ul>', 0),
(3, 'tests', NULL, 'tests', '', 1),
(4, 'DAE', NULL, '<p>Direction Appui aux Entreprises</p>', '<ul>\r\n	<li>Renforcer les capacit&eacute;s des entrepreneurs et de la comp&eacute;titivit&eacute; des entreprises&nbsp;</li>\r\n	<li>Promouvoir l&rsquo;esprit et de la gestion d&rsquo;entreprise aupr&egrave;s des promoteurs&nbsp;</li>\r\n	<li>D&eacute;velopper un programme d&rsquo;accompagnement adapt&eacute; aux entreprises&nbsp;</li>\r\n	<li>Apporter un appui technique aux entreprises et promoteurs via des programmes de formations en esprit d&rsquo;entreprise, cr&eacute;ation d&rsquo;entreprise, gestion d&rsquo;entreprise, montage de projets, recherche de financement, n&eacute;gociation et recherche de partenariat et management&nbsp;</li>\r\n	<li>Mettre en place et maintenir un centre de documentation pour fournir des informations sur le march&eacute; et les opportunit&eacute;s d&rsquo;investissement dans les secteurs &eacute;conomiques cl&eacute;s de la R&eacute;publique de Guin&eacute;e</li>\r\n	<li>Collecter et analyser des informations sur les co&ucirc;ts des facteurs et d&rsquo;identifier de nouvelles opportunit&eacute;s d&rsquo;investissement</li>\r\n	<li>Assister et accompagner les promoteurs priv&eacute;s guin&eacute;ens dans le d&eacute;veloppement de leurs projets ;</li>\r\n	<li>Entreprendre des recherches sur les perceptions des investisseurs nationaux et internationaux et sur les avantages offerts par les pays concurrents en collaboration avec les partenaires techniques</li>\r\n	<li>Planifier, organiser, coordonner, et suivre des ateliers de restitution sur les actions de recherche</li>\r\n	<li>Identifier et analyser les nouvelles niches de g&eacute;n&eacute;ration de Petites et Moyennes Entreprises</li>\r\n	<li>R&eacute;aliser des benchmarks pour capitaliser les bonnes pratiques en mati&egrave;re de prise en charge de Petites et Moyennes Entreprises</li>\r\n	<li>Rapprocher les investisseurs &eacute;trangers des promoteurs des Petites et Moyennes Entreprises/Petites et Moyennes Industries nationales pour favoriser le d&eacute;veloppement de celles-ci</li>\r\n	<li>Faire un plaidoyer en faveur des politiques d&rsquo;appui au secteur priv&eacute;</li>\r\n</ul>', 0),
(5, 'GU', NULL, '<p>Direction Guichet Unique</p>', '<ul>\r\n	<li>Immatriculer au registre du commerce et du cr&eacute;dit mobilier&nbsp;</li>\r\n	<li>D&eacute;livrer du num&eacute;ro d&rsquo;identification fiscale unique&nbsp;</li>\r\n	<li>Enregistrer et&nbsp; publier des actes de soci&eacute;t&eacute; et des noms commerciaux</li>\r\n	<li>Centraliser et accomplir les formalit&eacute;s administratives relatives &agrave; la cr&eacute;ation, &agrave; l&rsquo;extension, &agrave; la transmission et &agrave; la cessation d&rsquo;activit&eacute;s des entreprises&nbsp;</li>\r\n	<li>Enregistrer les d&eacute;clarations ou immatriculations pr&eacute;vues par les textes en vigueur en mati&egrave;re juridique, fiscale, administrative, commerciale, industrielle ou sociale&nbsp;</li>\r\n	<li>Accueillir et informer tout op&eacute;rateur &eacute;conomique sur les activit&eacute;s du Guichet Unique</li>\r\n	<li>Communiquer&nbsp; la liste des pi&egrave;ces &agrave; fournir selon la nature de la d&eacute;claration et l&rsquo;assurance de la recevabilit&eacute; de chaque dossier</li>\r\n	<li>Trairer les demandes de cr&eacute;ation d&rsquo;entreprise&nbsp;</li>\r\n	<li>Respecter les d&eacute;lais l&eacute;gaux de traitement des dossiers et d&rsquo;ex&eacute;cution des formalit&eacute;s requises avec les administrations et organismes concern&eacute;s</li>\r\n	<li>D&eacute;livrer le certificat de conformit&eacute; des statuts et de la carte professionnelle&nbsp;</li>\r\n	<li>D&eacute;livrer les d&eacute;clarations d&rsquo;existence</li>\r\n	<li>Initier, la proposition et la mise en &oelig;uvre toute action visant &agrave; am&eacute;liorer son organisation et son fonctionnement &agrave; travers une analyse permanente des besoins exprim&eacute;s par les op&eacute;rateurs &eacute;conomiques.</li>\r\n</ul>', 0),
(6, 'DPI', NULL, '<p>Direction Promotion des Investissements</p>', '<ul>\r\n	<li>Accueillir, informer, orienter et assister des investisseurs nationaux et &eacute;trangers&nbsp;</li>\r\n	<li>Collecter, traiter, produire et la diffuser aupr&egrave;s des investisseurs&nbsp; toute la documentation &eacute;conomique, juridique, fiscale, sociale, administrative et statistique tant en Guin&eacute;e qu&rsquo;&agrave; l&rsquo;&eacute;tranger</li>\r\n	<li>Contribuer &agrave; la mise en place des m&eacute;canismes de soutien et de financement pour les investisseurs&nbsp;</li>\r\n	<li>Constituer et&nbsp; g&eacute;rer une base de donn&eacute;es sur les secteurs prioritaires de l&rsquo;Etat</li>\r\n	<li>Rechercher &agrave; travers le monde, des investisseurs et des partenaires techniques, commerciaux et financiers&nbsp;</li>\r\n	<li>Elaborer des strat&eacute;gies de d&eacute;veloppement des investissements priv&eacute;s</li>\r\n	<li>Promouvoir les liens de coop&eacute;ration avec les structures similaires &eacute;trang&egrave;res&nbsp;</li>\r\n	<li>Promouvoir et attraction des investissements directs nationaux et &eacute;trangers en Guin&eacute;e</li>\r\n	<li>Promouvoir du Partenariat Public-Priv&eacute; aupr&egrave;s des investisseurs nationaux et &eacute;trangers&nbsp;</li>\r\n	<li>Rechercher et identifier des investisseurs et le d&eacute;veloppement des partenariats strat&eacute;giques&nbsp;</li>\r\n	<li>Participer &agrave; la promotion des investissements en Guin&eacute;e en relation avec les ambassades et consulats de la Guin&eacute;e &agrave; l&rsquo;&eacute;tranger</li>\r\n	<li>D&eacute;velopper des actions de communication et de marketing de la Guin&eacute;e comme destination pour l&rsquo;investissement par le biais des supports de communication ou des manifestations promotionnelles (s&eacute;minaires, forums, conf&eacute;rences, ateliers, foires et manifestations de promotion &eacute;conomique etc&hellip;)</li>\r\n	<li>Mettre en place un dispositif d&rsquo;attraction des investissements de la Diaspora</li>\r\n	<li>Prospecter activement des investisseurs priv&eacute;s&nbsp;</li>\r\n	<li>Appuyer et encadrer de fa&ccedil;on personalis&eacute;e pour facilitater les d&eacute;marches sur le terrain aux investisseurs</li>\r\n	<li>Concevoir des supports et outils de promotion et d&rsquo;outils d&rsquo;aide &agrave; la d&eacute;cision d&rsquo;investissement</li>\r\n	<li>Promouvoir activement des opportunit&eacute;s sectorielles d&rsquo;investissement en Guin&eacute;e &agrave; travers la veille et l&rsquo;intelligence marketing&nbsp;</li>\r\n	<li>D&eacute;velopper et maintenir les bases de donn&eacute;es sur les investisseurs, les secteurs porteurs et de l&rsquo;analyse des avantages comparatifs pour la Guin&eacute;e&nbsp;</li>\r\n</ul>', 0),
(7, 'ACDA', NULL, '<p>Direction Am&eacute;lioration Climat des Affaires</p>', '<ul>\r\n	<li>Identifier les difficult&eacute;s rencontr&eacute;es par les investisseurs dans la conduite des affaires</li>\r\n	<li>\r\n	<p>Acc&eacute;l&eacute;rer la mise en &oelig;uvre des r&eacute;formes pouvant permettre une plus grande s&eacute;curit&eacute; juridique et judiciaire des investisseurs et proposer au Gouvernement des mesures sp&eacute;cifiques sur l&rsquo;environnement des affaires et assurer le suivi et l&#39;&eacute;valuation de l&#39;ex&eacute;cution des mesures prises</p>\r\n	</li>\r\n	<li>\r\n	<p>Appuyer le Secr&eacute;tariat du Comit&eacute; de pilotage de l&rsquo;environnement des affaires&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Suivre les engagements et activit&eacute;s des partenaires techniques et financiers impactant l&rsquo;environnement des affaires</p>\r\n	</li>\r\n	<li>\r\n	<p>Analyser et de suivre les indicateurs Doing Business de la Banque mondiale</p>\r\n	</li>\r\n	<li>\r\n	<p>Proposer des initiatives de r&eacute;formes et assurer leur suivi en vue d&rsquo;am&eacute;liorer le classement de la Guin&eacute;e au Doing Business</p>\r\n	</li>\r\n</ul>', 0),
(8, 'DG', NULL, 'DIRECTION GENERALE', '', 1),
(9, 'DRH', NULL, 'Réssources Humaines', '', 1),
