INSERT INTO `exposes` (`id`, `exposant`, `details`, `id_reunion`, `supprimer`) VALUES
(1, 'Le Directeur Général', '<ul>\r\n	<li>&nbsp;Il a f&eacute;licit&eacute; tout d&rsquo;abord Monsieur Souar&eacute; au nom de l&rsquo;APIP ;</li>\r\n	<li>Il a &eacute;voqu&eacute; le conclave INDE-AFRIQUE, le d&eacute;roulement du dit &eacute;v&egrave;nement et les &eacute;ventuels retomb&eacute;es de ce forum ;</li>\r\n	<li>&nbsp;Sa participation au AFRICA CEO FORUM pendant lequel il a nou&eacute; les relations avec les agences de d&rsquo;autres pays Africains ;</li>\r\n	<li>Il a remerci&eacute; les cadres de l&rsquo;APIP qui ont contribu&eacute; &agrave; l&rsquo;organisation de la Table ronde sur les Zones Economiques et Sp&eacute;ciales ZES ;</li>\r\n	<li>Il a inform&eacute; les responsables de Direction et service de l&rsquo;Organisation prochaine d&rsquo;une</li>\r\n</ul>', 1, 0),
(2, 'Aliou Diallo', 'z', 1, 1),
(3, 'Appui aux entreprises', '<ul>\r\n	<li>Il a remerci&eacute; la direction et tous les travailleurs de l&rsquo;APIP et a par la m&ecirc;me occasion magnifi&eacute; l&rsquo;honneur d&rsquo;avoir appartenu &agrave; une tr&egrave;s grande &eacute;quipe au sein de l&rsquo;agence&nbsp;;</li>\r\n</ul>', 1, 0),
(4, 'Antennes régionales', '<ul>\r\n	<li>Il a demand&eacute; &agrave; la direction de r&eacute;fl&eacute;chir sur les moyens permettant de retenir un peu plus longtemps les cadres de l&rsquo;APIP ;</li>\r\n</ul>', 1, 0),
(5, 'Consultant RH', '<ul>\r\n	<li>Il a inform&eacute; les responsables qu&rsquo;il a re&ccedil;u des CV de personnes vivant &agrave; l&rsquo;Etranger ou qui r&eacute;sident en Guin&eacute;e pour le poste de Directeur a la Directeur promotion des Investissements</li>\r\n	<li>Il a d&eacute;j&agrave; commenc&eacute; les entretiens avec certains candidats pour ce poste</li>\r\n</ul>', 1, 0),
(6, 'Département Guichet Unique', '<ul>\r\n	<li>Elle pense &eacute;galement que l&rsquo;APIP ne doit pas seulement organiser des &eacute;v&egrave;nements mais chercher &agrave; tirer son &eacute;pingle du jeu ;</li>\r\n	<li>Elle a inform&eacute; les responsables qu&rsquo;il y a dix-sept personnes qui doivent venir pour des stages dans le cadre du projet de formalisation mobile</li>\r\n</ul>', 1, 0),
(7, 'Project Manager', '<ul>\r\n	<li>Il a fait le compte rendu de la rencontre avec le repr&eacute;sentant pays du PNUD qui a d&eacute;cid&eacute;</li>\r\n</ul>', 1, 0),
(8, 'kallo', '<p>Finalisation des d&eacute;veloppement</p>', 3, 0),
(9, 'Alousseny Ousmane Kallo (Directeur DESI)', '<p>D&eacute;ploiement module SMS</p>', 4, 0),
(10, 'Oumar Bocoum', '<p>Rebranding site APIP</p>', 4, 0),
(11, 'ljkhgjdvjsd', '<p>,.dlsjnbkjbksdj</p>', 5, 0),
(12, 'Le Directeur Général', '<p>- Il a fait le compte rendu du conseil de cabinet tenu au Minist&egrave;re</p>\r\n\r\n<p>- Il a demand&eacute; &agrave; la DESI de s&rsquo;assurer que le site de l&rsquo;APIP n&rsquo;est pas hacker</p>\r\n\r\n<p>- Dans le cadre de la transmission des r&eacute;ponses aux questionnaires DOING BUSINESS, il a demand&eacute; au Directeur de de la direction Am&eacute;lioration du Climat des Affaires de finaliser les r&eacute;ponses du Ministre et de les envoyer</p>\r\n\r\n<p>- Il a demand&eacute; au directeur de la DACA d&rsquo;organiser une matinale chaque semaine en fonction des indicateurs</p>\r\n\r\n<p>- Il a inform&eacute; les responsables presents de l&rsquo;organisation ce weekend dans les locaux de l&rsquo;APIP du conseil d&rsquo;administration du Fonds Fiduciaire d&rsquo;Urgence du programme Integra</p>', 6, 0),
(13, 'Directrice Générale Adjointe', '<p>- Elle a fait le point sur le partenariat APIP-OIM qui est d&eacute;j&agrave; en cours et pour lequel des agents ont &eacute;t&eacute; recrut&eacute;s dans le cadre de la mise en &oelig;uvre d&rsquo;un projet commun</p>\r\n\r\n<p>- Elle a fait le point sur l&rsquo;&eacute;tat d&rsquo;avancement des pr&eacute;paratifs du GUIF</p>\r\n\r\n<p>- Elle a fait le point sur la participation des femmes de l&rsquo;APIP aux c&ocirc;t&eacute;s de celles du minist&egrave;re lors de la c&eacute;l&eacute;bration de la journ&eacute;e internationale des droits des femmes</p>', 6, 0),
(14, 'Consultant Expert Ressources Humaines', '<p>- Il a fait le point sur le plan de formation qui est en cours d&rsquo;&eacute;laboration pour certains agents de l&rsquo;agence</p>', 6, 0),
(15, 'Directeur Adjoint Etudes, Statistiques et Innovation', '<p>- Il a pr&eacute;sent&eacute; l&rsquo;application que sa direction a mis en place afin de centraliser tous les plans d&rsquo;action des diff&eacute;rentes directions. Cette application comprend des onglets tels que les rapports, l&rsquo;&eacute;tat d&rsquo;avancement, le mode de financement mais &eacute;galement un onglet suivi et &eacute;valuation</p>', 6, 0),
(16, 'Directeur Etudes, Statistiques et Innovation', '<p>- Il a fait le compte rendu des activit&eacute;s au sein de sa direction</p>\r\n\r\n<p>- Il a inform&eacute; la direction que le tribunal de commerce est d&eacute;j&agrave; pr&ecirc;t &agrave; mettre &agrave; disposition les greffiers et les mat&eacute;riels dans le cadre du RCCM-OHADA</p>\r\n\r\n<p>- Pour ce qui est de la connexion au niveau du nouveau bureau affect&eacute; &agrave; certains agents de l&rsquo;APIP, il a inform&eacute; la direction que des cl&eacute;s seront mis &agrave; disposition de chacun de ces agents</p>', 6, 0),
(17, 'Directeur Adjoint Guichet Unique', '<p>- Il a inform&eacute; la direction qu&rsquo;il y a des dossiers en instance dont le retard est d&ucirc; au manque de disponibilit&eacute; des NIF</p>\r\n\r\n<p>- Il a inform&eacute; la direction que le budget relatif &agrave; l&rsquo;APIP-MOBILE a &eacute;t&eacute; finalis&eacute; et transmis au coordinateur des projets et partenariats</p>', 6, 0),
(18, 'Directrice assistance aux entreprises', '<p>- Elle a fait le compte rendu des activit&eacute;s en cours au sein de sa direction, plus particuli&egrave;rement les visites de terrain</p>\r\n\r\n<p>- Elle a inform&eacute; la direction de la disponibilit&eacute; d&rsquo;un stand de l&rsquo;APIP pour le SADEN</p>', 6, 0),
(19, 'Directeur Amélioration du climat des affaires', '<p>- Il a fait le point sur l&rsquo;&eacute;tat d&rsquo;avancement des activit&eacute;s au sein de sa direction</p>\r\n\r\n<p>- Il a inform&eacute; la direction que tous les contributeurs ont d&eacute;j&agrave; &eacute;t&eacute; contact&eacute; pour la transmission des questionnaires DOING BUSINESS</p>\r\n\r\n<p>- Il a donn&eacute; des explications sur la retraite que sa direction compte organiser dans le but de communiquer sur l&rsquo;importance du classement DOING BUSINESS</p>', 6, 0),
(20, 'Directrice Promotion des Investissements', '<p>- Elle a fait le compte rendu des activit&eacute;s en cours au sein de sa direction</p>\r\n\r\n<p>- Elle a fait part de l&rsquo;annulation de certains forums auxquels l&rsquo;APIP devait prendre part</p>', 6, 0),
(21, 'Responsable service Communication et Marketing', '<p>- Il a fait le point sur les s&eacute;ances de shootings en cours au sein de l&rsquo;agence et l&rsquo;objectif vis&eacute; au terme de cette activit&eacute;</p>\r\n\r\n<p>- Il a donn&eacute; des informations sur les pr&eacute;paratifs cot&eacute; communication du GUIF</p>', 6, 0),
(22, 'Coordinateur Entreprenariat Rural', '<p>- Il a donn&eacute; des explications sur les diff&eacute;rentes rencontres que son service a eu avec certains partenaires techniques financiers</p>', 6, 0),
(23, 'desi', '<p>ljljngpjnsmgdom</p>', 7, 0),
(24, 'Le Directeur Général', '<p>- Il a fait le point sur le conseil de cabinet qui s&rsquo;est tenu ce matin au minist&egrave;re en charge des Investissements et des PPP</p>\r\n\r\n<p>- Il a fait une br&egrave;ve intervention sur le covid-19 et les mesures &agrave; recommander aux directeurs de respecter et de faire respecter les consignes sanitaires pour &eacute;viter ladite maladie</p>\r\n\r\n<p>- Il a fait un compte rendu de la r&eacute;union du conseil d&rsquo;administration du Fonds Fiduciaire d&rsquo;Urgence du&nbsp;nouvel indicateur sur les march&eacute;s publics</p>', 9, 0),
(25, 'Directrice Promotion des Investissements', '<p>- Elle a fait le point sur les pr&eacute;paratifs de l&rsquo;Expo 2020 en collaboration avec le MIPPP</p>\r\n\r\n<p>- Elle a inform&eacute; la direction de l&rsquo;int&eacute;r&ecirc;t croissant de certaines ambassades notamment celles du Ghana et de l&rsquo;Afrique du Sud ainsi que celle de la Guin&eacute;e aux Etats-Unis pour le GUIF</p>\r\n\r\n<p>- Dans le cadre du GUIF, elle a inform&eacute; la direction de l&rsquo;engagement d&rsquo;Afriland First Bank qui a confirm&eacute; qu&rsquo;il y aura une signature de contrats de financement de projets &agrave; hauteur de 7 millions de dollars. La Soci&eacute;t&eacute; G&eacute;n&eacute;rale a &eacute;galement donn&eacute; son accord pour des signatures pendant le GUIF</p>', 9, 0),
(26, 'Responsable service Communication et Marketing', '<p>- Il a fait le compte rendu des activit&eacute;s en cours au niveau de son service</p>\r\n\r\n<p>- Il a donn&eacute; des informations sur les pr&eacute;paratifs cot&eacute; communication du GUIF</p>', 9, 0),
(27, 'Coordinateur Entreprenariat Rural', '<p>- Il a fait le compte rendu des diff&eacute;rentes activit&eacute;s en cours au niveau de son service</p>\r\n\r\n<p>- Il a fait le point sur les rencontres qu&rsquo;il a eu avec certains partenaires techniques et plus particuli&egrave;rement l&rsquo;OIM</p>\r\n\r\n<p>- Programme INTEGRA qui s&rsquo;est tenue &agrave; l&rsquo;APIP ce weekend</p>', 9, 0),
(28, 'Directrice Générale Adjointe', '<p>- Elle a donn&eacute; des explications sur le nouvel outil mis en place par la DESI dans le cadre de la r&eacute;alisation des activit&eacute;s trimestrielles de chaque direction.</p>\r\n\r\n<p>- Elle a fait le point sur la disponibilit&eacute; des PAO des diff&eacute;rentes directions</p>', 9, 0),
(29, 'Controleure Financier', '<p>- Elle a fait une pr&eacute;sentation sur les nouvelles proc&eacute;dures d&rsquo;ex&eacute;cution des d&eacute;penses qui d&eacute;sormais devra &ecirc;tre suivi au sein de l&rsquo;agence</p>', 9, 0),
(30, 'Directeur Etudes, Statistiques et Innovation', '<p>- Il a fait le point sur les diff&eacute;rentes activit&eacute;s de sa direction</p>\r\n\r\n<p>- Il a inform&eacute; la direction que son &eacute;quipe travaille en collaboration avec le conseiller juridique sur la finalisation des contrats tel que recommand&eacute; par le partenaire financier (BID) dans le cadre du rebranding de l&rsquo;agence</p>\r\n\r\n<p>- Il a inform&eacute; la direction que le syst&egrave;me de sms au d&eacute;p&ocirc;t et au retrait des dossiers est disponible</p>', 9, 0),
(31, 'Directeur Adjoint Guichet Unique', '<p>- Il a inform&eacute; la direction que cette ann&eacute;e malgr&eacute; certains probl&egrave;mes, il y a eu beaucoup de plus de cr&eacute;ations d&rsquo;entreprises &agrave; ce jour</p>\r\n\r\n<p>- Il a fait le point sur la situation des besoins au niveau des diff&eacute;rentes annexes de l&rsquo;agence</p>', 9, 0),
(32, 'Directeur Amélioration du climat des affaires', '<p>- Il a inform&eacute; la direction de la r&eacute;ception des PV des inspections conjointes au niveau du minist&egrave;re de la Ville et de l&rsquo;Am&eacute;nagement du Territoire</p>\r\n\r\n<p>- Il a fait le compte rendu des activit&eacute;s en cours au sein de la direction Am&eacute;lioration du Climat des Affaires</p>\r\n\r\n<p>- Il a inform&eacute; la direction que DOING BUSINESS a ajout&eacute; un&nbsp;nouvel indicateur sur les march&eacute;s publics</p>', 9, 0),
(33, 'Directrice Promotion des Investissements', '<p>- Elle a fait le point sur les pr&eacute;paratifs de l&rsquo;Expo 2020 en collaboration avec le MIPPP</p>\r\n\r\n<p>- Elle a inform&eacute; la direction de l&rsquo;int&eacute;r&ecirc;t croissant de certaines ambassades notamment celles du Ghana et de l&rsquo;Afrique du Sud ainsi que celle de la Guin&eacute;e aux Etats-Unis pour le GUIF</p>\r\n\r\n<p>- Dans le cadre du GUIF, elle a inform&eacute; la direction de l&rsquo;engagement d&rsquo;Afriland First Bank qui a confirm&eacute; qu&rsquo;il y aura une signature de contrats de financement de projets &agrave; hauteur de 7 millions de dollars. La Soci&eacute;t&eacute; G&eacute;n&eacute;rale a &eacute;galement donn&eacute; son accord pour des signatures pendant le GUIF</p>', 9, 0),
(34, 'Responsable service Communication et Marketing', '<p>- Il a fait le compte rendu des activit&eacute;s en cours au niveau de son service</p>\r\n\r\n<p>- Il a donn&eacute; des informations sur les pr&eacute;paratifs cot&eacute; communication du GUIF</p>', 9, 0),
(35, 'Coordinateur Entreprenariat Rural', '<p>- Il a fait le compte rendu des diff&eacute;rentes activit&eacute;s en cours au niveau de son service</p>\r\n\r\n<p>- Il a fait le point sur les rencontres qu&rsquo;il a eu avec certains partenaires techniques et plus particuli&egrave;rement l&rsquo;OIM</p>', 9, 0),
(36, 'Namory CAMARA', '<p>- Il a remerci&eacute; les uns et les autres pour les&nbsp;</p>\r\n\r\n<p>- Il a expliqu&eacute; le travail de fond qui a&nbsp;&eacute;t&eacute; abattu dans le cadre du plan d&#39;action&nbsp;&eacute;labor&eacute; par l&#39;APIP et valid&eacute; par la primature dans le cadre de la riposte &eacute;conomique a la pand&eacute;mie du covid-19</p>', 17, 0),
(37, 'Lamine SOLANO', '<p>- Il a donn&eacute; des informations sur le memorandum en cours de redaction sur le Doing Business et a expliqu&eacute;&nbsp; les difficult&eacute;s rencontr&eacute;es d&#39;aller dans les minist&egrave;res pour cause de covid-19</p>\r\n\r\n<p>- Pour ce qui est de la conservation fonci&egrave;re, il a informe la direction que le document est&nbsp;&agrave; la signature. Pour les autres documents tout est ok</p>', 17, 0),
(38, 'Salimatou BARRY', '<p>- Elle a inform&eacute; la direction sur le travail de renforcement de capacit&eacute;s qu&#39;elle a initi&eacute; au niveau de sa direction et pour lequel chaque charg&eacute; de promotion devrait faire une presentation</p>\r\n\r\n<p>- La direction travaille egalement sur le plan de la BID</p>\r\n\r\n<p>- La direction appuie egalement l&#39;alliance sanitaire pour la mobilisation des fonds dans le cadre du plan de riposte</p>', 17, 0),
(39, 'Diana KOUYATE', '<p>-&nbsp;</p>', 17, 0),
(40, 'Mme Challoub Yolande COLLE', '<p>- Elle a donn&eacute; des explications sur l&#39;outil de travail en cours d&#39;elaboration au niveau de la DAE</p>\r\n\r\n<p>- Elle compte organiser une formation complementaire pour trois entreprises dans le cadre du programme accelerateur</p>', 17, 0),
(41, 'Alousseny KALLO', '<p>- En reaction a la problematique posee par la Directrice de la DAE, il a suggere d&#39;aller directement vers la CNSS pour avoir la base de donnees relative au nombre d&#39;employes par entreprise</p>', 17, 0),
(42, 'Mamadou Saliou SYLLA', '<p>- Son equipe travaille&nbsp;&agrave; la finalisation du guide de l&#39;investisseur en collaboration avec la DPI</p>\r\n\r\n<p>- Travaille aussi sur les outils du rebranding</p>', 17, 0),
(43, 'Cellou BARRY', '<p>- Il a donne des explications sur le plan d&#39;action que l&#39;APIP en collaboration avec le PNUD a soumis a la primature dans le cadre du plan de riposte du gouvernement dans son volet appui au secteur prive</p>', 17, 0),
(44, 'Le Directeur Général', '<ul>\r\n	<li>Il a remerciee les directeurs pour leur presence et leur familiarisation aux nouveaux outils de communication permettant de tenir des reunions malgre la situation actuelle et depuis les domiciles&nbsp;;</li>\r\n	<li>Il a partagee avec les directeurs sur la vision qu&rsquo;il a de la plateforme devant etre mis en place en terme d&rsquo;accompagnement des entreprises avec l&rsquo;implication de certaines structures d&rsquo;Etat&nbsp;;</li>\r\n	<li>Il a demand&eacute; &agrave; la DAE et &agrave; la DESI de r&eacute;fl&eacute;chir &agrave; l&rsquo;introduction de d&rsquo;autres approches et de options d&rsquo;accompagnement sur la plateforme&nbsp;;</li>\r\n	<li>Il a donn&eacute; des explications sur l&rsquo;objectif visee par la direction G&eacute;n&eacute;rale en voulant pas faire payer par les promoteurs des frais additionnels li&eacute;s &agrave; l&rsquo;application mobile de cr&eacute;ation en ligne.</li>\r\n</ul>', 18, 0),
(45, 'Directrice Générale Adjointe', '<ul>\r\n	<li>Elle a indiqu&eacute; que pour un meilleur suivi des activit&eacute;s des antennes r&eacute;gionales, une personne a &eacute;t&eacute; d&eacute;sign&eacute;e &agrave; cet effet pour r&eacute;gler toutes les questions y aff&eacute;rentes&nbsp;;</li>\r\n	<li>Elle a demand&eacute; aux diff&eacute;rents directeurs de mettre &agrave; jour les diff&eacute;rents plans d&rsquo;actions afin que ceux-ci soient consolid&eacute;s et envoy&eacute;s au MIPPP le plus t&ocirc;t que possible.</li>\r\n</ul>', 18, 0),
(46, 'Directeur Etudes, Statistiques et Innovation', '<ul>\r\n	<li>Il a vant&eacute; les m&eacute;rites de la nouvelle plateforme que la DAE utilise actuellement pour la formation&nbsp;de certains entrepreneurs et souhaite vivement s&rsquo;en inspirer pour doter l&rsquo;agence d&rsquo;un outil de ce genre ;</li>\r\n	<li>Il a conseill&eacute; a la DAE d&rsquo;aller au-del&agrave; de ce pourquoi l&rsquo;actuel plateforme est utilis&eacute;e et sur laquelle la DNI compte &eacute;galement apporter des informations suppl&eacute;mentaires pour les entreprises&nbsp;;</li>\r\n	<li>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que les premiers tests de l&rsquo;application de paiement mobile pour les cr&eacute;ations d&rsquo;entreprises en ligne, seront effectu&eacute;s la semaine prochaine&nbsp;;</li>\r\n	<li>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que son &eacute;quipe travaille sur la plateforme devant &ecirc;tre utilis&eacute;e par les membres du jury dans le cadre de la comp&eacute;tition sur les business plan. Une premi&egrave;re maquette devrait &ecirc;tre disponible les jours qui suivent selon ses dires.</li>\r\n</ul>', 18, 0),
(47, 'Le Directeur Général', '<ul>\r\n	<li>Il a fait le compte rendu du conseil de cabinet du minist&egrave;re en charge des investissements et des Partenariats Publics-Priv&eacute;s&nbsp;;</li>\r\n	<li>Il a inform&eacute; les directeurs du focus que fait d&eacute;sormais le minist&egrave;re sur l&rsquo;indicateur millenium challenge par rapport auquel des s&eacute;ances de sensibilisations vont bient&ocirc;t commencer&nbsp;;</li>\r\n</ul>\r\n\r\n<p>&nbsp;Il a f&eacute;licit&eacute; toute l&rsquo;&eacute;quipe impliqu&eacute;e dans l&rsquo;organisation du webinaire du vendredi 12 juin dernier et a exhort&eacute; toutes les directions &agrave; travailler dans le sens de la p&eacute;rennisation de ce type d&rsquo;&eacute;v&egrave;nement.</p>', 20, 0),
(48, 'Directrice Générale Adjointe', '<p>Elle a f&eacute;licit&eacute; tous ceux qui de pr&egrave;s ou de loin ont contribu&eacute; &agrave; la r&eacute;ussite du webinaire et s&rsquo;est dite fier de voir qu&rsquo;une agence gouvernementale arrive &agrave; r&eacute;aliser un tel &eacute;v&egrave;nement&nbsp;;</p>', 20, 0),
(49, 'Directeur Amélioration Climat des Affaires', '<p>Il a exprim&eacute; le souhait que les recommandations issues de l&rsquo;&eacute;tude sur la mortalit&eacute; telle que le suivi des entreprises par l&rsquo;APIP soit int&eacute;gr&eacute; aux activit&eacute;s du guichet unique. Il a &eacute;galement propos&eacute; &agrave; la direction g&eacute;n&eacute;rale de p&eacute;renniser ce type d&rsquo;&eacute;v&egrave;nement chaque deux ou trois mois&nbsp;;</p>', 20, 0),
(50, 'Directrice Promotion des Investissements', '<p>Elle a f&eacute;licit&eacute; l&rsquo;&eacute;quipe pour l&rsquo;organisation du webinaire et souhaite que l&rsquo;agence continue &agrave; innover et &agrave; se mette aux nouvelles technologies en s&rsquo;adaptant &agrave; la nouvelle donne ;</p>', 20, 0),
(51, 'Directrice Appui aux entreprises', '<ul>\r\n	<li>Elle a propos&eacute; que pour les prochains webinaires que l&rsquo;agence organisera, qu&rsquo;il y est une &eacute;quipe constitu&eacute;e de chaque direction pour r&eacute;pondre aux diff&eacute;rentes questions des participants&nbsp;;</li>\r\n</ul>\r\n\r\n<p>Elle a fait le point sur les r&eacute;sultats du concours li&eacute; aux business plans.</p>', 20, 0),
(52, 'Directeur Etude, Statistiques et Innovation', '<ul>\r\n	<li>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que les derniers tests de l&rsquo;application du paiement mobile ont &eacute;t&eacute; concluants et compte en finir d&eacute;finitivement au plus tard &agrave; la fin de ce mois&nbsp;;</li>\r\n</ul>\r\n\r\n<p>Il a &eacute;galement inform&eacute; la Direction de la fin des travaux de mise en &oelig;uvre du nouveau site de l&rsquo;APIP.</p>', 20, 0),
(53, 'Responsable Communication et Marketing', '<p>Il a signal&eacute; les nombreuses difficult&eacute;s auxquelles son service fait face, notamment un manque criard de personnel qualifi&eacute; qui se fait ressentir au fur et &agrave; mesure que les activit&eacute;s &agrave; r&eacute;aliser se pr&eacute;sentent.</p>', 20, 0),
(54, 'Le Directeur Général', '<p>Il a tout d&rsquo;abord exhort&eacute; les directeurs &agrave; continuer &agrave; maintenir vigilance dont ils ont fait preuve pour se pr&eacute;server et pr&eacute;server leurs &eacute;quipes du covid-19 en respectant les gestes barri&egrave;res ;</p>\r\n\r\n<p>Il a inform&eacute; les directeurs du compte rendu qu&rsquo;il a fait du partenariat avec le PDAIG lors du conseil de cabinet de ce lundi 09 Novembre 2020 au Minist&egrave;re en charge des Investissements et Partenariats Publics-Priv&eacute;s ;</p>\r\n\r\n<p>Il a inform&eacute; les directeurs du compte rendu que la Secr&eacute;taire G&eacute;n&eacute;rale du Minist&egrave;re de tutelle a fait de sa rencontre avec l&rsquo;&eacute;quipe DOING BUSINESS relative aux six (6) nouveaux indicateurs que SFI compte prendre en compte sans en avoir avis&eacute; la Guin&eacute;e. Cette d&eacute;marche n&rsquo;a pas du tout &eacute;t&eacute; appr&eacute;ci&eacute;e par le Ministre Gabriel Curtis qui compte saisir officiellement la Banque Mondiale&nbsp;pour exprimer sa d&eacute;sapprobation ;</p>\r\n\r\n<p>Il a inform&eacute; les directeurs que le Ministre en charge des investissements compte &eacute;norm&eacute;ment sur l&rsquo;APIP pour la mise en &oelig;uvre du projet de cr&eacute;ation de cent mille (100.000) entrepreneurs du pr&eacute;sident de la r&eacute;publique sur une p&eacute;riode de trois (3) ans. Il a par cons&eacute;quent demand&eacute; aux directeurs de pousser la r&eacute;flexion afin de mettre d&eacute;j&agrave; en place un d&eacute;but de concr&eacute;tisation dudit projet&nbsp;;</p>\r\n\r\n<p>Il a inform&eacute; les directeurs de l&rsquo;approbation du budget final du GUIF par la BAD et en a profit&eacute; pour f&eacute;liciter tout le monde tout en demandant &agrave; chacun de continuer &agrave; travailler en &eacute;quipe car selon lui c&rsquo;est ce travail d&rsquo;&eacute;quipe qui a permis d&rsquo;arriver &agrave; ce r&eacute;sultat qui a plac&eacute; la Guin&eacute;e deuxi&egrave;me (2eme) pour ce qui est des projets soumis &agrave; la BAD.</p>', 21, 0),
(55, 'Directrice Général Adjointe', '<p>Elle a inform&eacute; les directeurs qu&rsquo;elle travaille avec le conseiller juridique sur la pr&eacute;paration de l&rsquo;assembl&eacute;e g&eacute;n&eacute;rale pour lequel elle aura besoin de la finalisation des plans d&rsquo;action par direction pour l&rsquo;ann&eacute;e 2020&nbsp;;</p>\r\n\r\n<p>Elle a invit&eacute; les directeurs les directeurs &agrave; faire leur mieux afin que le Plan d&rsquo;Action Op&eacute;rationnel consolid&eacute; de l&rsquo;APIP soit disponible dans les plus brefs d&eacute;lais.</p>', 21, 0),
(56, 'Directeur Amélioration Climat des Affaires', '<p>Il a inform&eacute; les directeurs de l&rsquo;organisation prochaine d&rsquo;une matinale avec les notaires sur la r&eacute;servation de nom et pour lequel SFI appui l&rsquo;APIP ;</p>\r\n\r\n<p>Il a fait le compte rendu de la campagne de communication sur laquelle son &eacute;quipe travaille en collaboration avec Monarch. Cette campagne est relative a l&rsquo;indicateur r&eacute;servation de nom et pour lequel des flyers doivent &ecirc;tre fait et des spots publicitaires con&ccedil;us.</p>', 21, 0),
(57, 'Directrice Promotion des Investissements', '<p>Elle a inform&eacute; la Direction G&eacute;n&eacute;rale du partenariat en cours avec le Minist&egrave;re de la p&ecirc;che et de l&rsquo;Aquaculture dans le but d&rsquo;organiser une table ronde des bailleurs afin d&rsquo;assurer une gestion durable des ressources halieutiques en Guin&eacute;e ;</p>\r\n\r\n<p>Dans le cadre de la r&eacute;alisation du GUIF elle a fait le compte rendu de l&rsquo;&eacute;tat d&rsquo;avancement dudit &eacute;v&egrave;nement et des &eacute;changes qu&rsquo;elle a eus avec les partenaires de l&rsquo;&eacute;v&egrave;nement ;</p>', 21, 0),
(58, 'Directrice Appui aux entreprises', '<p>Elle a fait le compte rendu des activit&eacute;s relative &agrave; la mise en &oelig;uvre du programme YouthConnekt for Women qui couvre pour le moment la base cote et pour lequel il y a eu 983 projets recueillis ;</p>\r\n\r\n<p>Elle a inform&eacute; la Direction G&eacute;n&eacute;rale de la fin de l&rsquo;appel a candidature pour la s&eacute;lection de 50 business plan&nbsp;;</p>', 21, 0),
(59, 'Coordinateur Programme et Projets', '<p>Dans le cadre du programme de 100 mille entrepreneurs, il a propos&eacute; que les jeunes et ou entreprises &agrave; accompagner soient segmenter en fonction du type d&rsquo;entreprises que ces personnes souhaitent cr&eacute;er.</p>\r\n\r\n<p>Il a pr&eacute;sent&eacute; son nouveau collaborateur en la personne de Monsieur Mohamed Lamine Toure qui est d&eacute;sormais charg&eacute; de projet au niveau de la Coordination Programme et Projets</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que son &eacute;quipe travaille &agrave; la r&eacute;alisation d&rsquo;un projet en lien avec l&rsquo;APIP-MOBILE dans le but d&rsquo;assurer une continuit&eacute; dudit projet.</p>', 21, 0),
(60, 'Directeur Adjoint Guichet Unique', '<p>Il a donn&eacute; les chiffres de cr&eacute;ations d&rsquo;entreprises pour la p&eacute;riode en cours.</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale de la mise &agrave; jour des nouvelles fiches de renseignements</p>\r\n\r\n<p>Il a &eacute;galement inform&eacute; la Direction G&eacute;n&eacute;rale de la r&eacute;union pr&eacute;vue avec le Directeur Nationale Adjoint des Imp&ocirc;ts pour essayer de r&eacute;gler d&eacute;finitivement les probl&egrave;mes li&eacute;s &agrave; la d&eacute;livrance des Num&eacute;ro d&rsquo;Immatriculation Fiscale (NIF)</p>', 21, 0),
(61, 'Directeur Etudes, Statistiques et Innovation', '<p>Il a fait le compte rendu des s&eacute;ances de travail que son &eacute;quipe a eu avec la Direction Nationale des Imp&ocirc;ts dans le cadre de la d&eacute;mat&eacute;rialisation des cr&eacute;ations et des le&ccedil;ons qu&rsquo;ils en ont tir&eacute; ;</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que son &eacute;quipe a finalis&eacute; le site internet qui sera pr&eacute;sent&eacute; ce vendredi par en collaboration avec l&rsquo;&eacute;quipe Communication et Marketing le cadre du rebranding de l&rsquo;APIP et mettra un accent particulier sur tous les changements qui vont avec ;</p>\r\n\r\n<p>Il a &eacute;galement inform&eacute; la Direction G&eacute;n&eacute;rale que plusieurs entit&eacute;s ont d&eacute;j&agrave; adress&eacute; des courriers &agrave; l&rsquo;APIP pour la mise &agrave; disposition de certaines donn&eacute;es que la DESI compte vendre dans le cadre de l&rsquo;APIP-ANALYTICS.</p>', 21, 0),
(62, 'Le Directeur Général', '<p>Il a inform&eacute; les directeurs du passage ce mercredi 18 Novembre d&rsquo;une &eacute;quipe de l&rsquo;ANSS dans le but de proc&eacute;der au test de d&eacute;pistage covid-19 de tout le personnel soit conform&eacute;ment aux directives du gouvernement&nbsp;;</p>\r\n\r\n<p>Il a exhort&eacute; la DPI &agrave; faire du hotspot leur outil de pr&eacute;dilection afin de pouvoir y extraire un tableau de suivi des investisseurs qui ont d&eacute;cid&eacute; de s&rsquo;&eacute;tablir ou qui ont sign&eacute; des partenariats avec d&rsquo;autres entreprises guin&eacute;ennes dans le but de d&eacute;marrer leurs activit&eacute;s ;</p>\r\n\r\n<p>Il a f&eacute;licit&eacute; tous ceux qui ont travaill&eacute; &agrave; la mise en &oelig;uvre du nouveau site internet de l&rsquo;APIP et en a profit&eacute; pour demander au service communication et &agrave; la DPI de mettre un accent particulier sur le marketing digital pour l&rsquo;attraction des investisseurs.</p>', 22, 0),
(63, 'Directrice Général Adjointe', '<p>Elle a inform&eacute; les directeurs de la mise en place d&rsquo;un tableau de suivi qui sera d&eacute;sormais utilis&eacute; au cours des prochaines r&eacute;unions afin que la Direction G&eacute;n&eacute;rale ait une certaine vue de l&rsquo;&eacute;volution des activit&eacute;s&nbsp;;</p>\r\n\r\n<p>Elle a fait le compte rendu des entretiens individuels qu&rsquo;elle a eus avec l&rsquo;&eacute;quipe du Guichet par rapport &agrave; l&rsquo;assiduit&eacute; et &agrave; la ponctualit&eacute; de cette derni&egrave;re afin de les rendre encore plus professionnels. L&rsquo;autre objectif de ces entretiens est de diminuer l&rsquo;utilisation du papier afin que synergui soit utilis&eacute; au maximum;</p>\r\n\r\n<p>Elle a fait le compte rendu des travaux pr&eacute;paratoires li&eacute;s &agrave; l&rsquo;organisation du Guinea Investments Forum (GUIF) et de la mise en place &agrave; cet effet des diff&eacute;rentes commissions.</p>', 22, 0),
(64, 'Directeur Amélioration Climat des Affaires', '<p>Dans le cadre de l&rsquo;organisation de la matinale du 26 Novembre, il a inform&eacute; la Direction G&eacute;n&eacute;rale de l&rsquo;envoi des lettres d&rsquo;invitation aux diff&eacute;rents notaires choisis ;</p>\r\n\r\n<p>Il a fait le compte rendu de la r&eacute;union avec le nouveau Directeur G&eacute;n&eacute;ral de l&rsquo;EDG relative &agrave; l&rsquo;indicateur acc&egrave;s &agrave; l&rsquo;&eacute;lectricit&eacute;. Pendant cette r&eacute;union ils ont &eacute;galement &eacute;voqu&eacute; les probl&egrave;mes li&eacute;s &agrave; SAIDI et SAIFI&nbsp;;</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale de l&rsquo;organisation prochaine dans la premi&egrave;re semaine du mois de d&eacute;cembre d&rsquo;une matinale relative &agrave; l&rsquo;indicateur paiement de taxes avec certains cabinets de la place.</p>', 22, 0),
(65, 'Directeur Adjoint Promotion des Investissements', '<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale du travail que son &eacute;quipe continue de faire dans le cadre de la pr&eacute;paration du Guinea Investments Forum (GUIF). La mise &agrave; disposition de la liste des sponsors est l&rsquo;&eacute;tape qui est actuellement en cours de finalisation ;</p>', 22, 0),
(66, 'Chargé de projets', '<p>Dans le cadre de son int&eacute;gration au sein de l&rsquo;APIP, il a inform&eacute; la Direction G&eacute;n&eacute;rale qu&rsquo;il a commenc&eacute; &agrave; s&rsquo;immerger dans certaines directions afin d&rsquo;appr&eacute;hender au mieux le travail de chacune d&rsquo;elle. Pour le moment, il a visit&eacute; le Climat des Affaires et la DAE&nbsp;;</p>\r\n\r\n<p>Il a &eacute;galement inform&eacute; la Direction G&eacute;n&eacute;rale de la mise en place prochaine d&rsquo;un tableau de suivi de tous les bailleurs et partenaires avec lesquels les diff&eacute;rentes directions de l&rsquo;agence ont d&eacute;j&agrave; travaill&eacute; et d&rsquo;autres potentiels partenaires.</p>', 22, 0),
(67, 'Directeur Adjoint Guichet Unique', '<p>Il a donn&eacute; des explications sur les difficult&eacute;s qu&rsquo;ils ont dans la cr&eacute;ation d&rsquo;entreprises relative &agrave; certaines formes juridiques et de la r&eacute;union qu&rsquo;il doit avoir en compagnie de la DGA avec Monsieur Kakoro de la Direction Nationale des Imp&ocirc;ts.</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale de la baisse des cr&eacute;ations qui selon lui est due &agrave; l&rsquo;expiration des NIFs en cette fin d&rsquo;ann&eacute;e. Ce pourquoi, beaucoup pr&eacute;f&egrave;rent attendre la nouvelle ann&eacute;e.</p>', 22, 0),
(68, 'Directeur Etudes, Statistiques et Innovation', '<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale du lancement&nbsp;ce vendredi du site internet de l&rsquo;APIP ;</p>\r\n\r\n<p>Il a inform&eacute; la Direction G&eacute;n&eacute;rale que son &eacute;quipe travaille &agrave; la finalisation de la liaison de l&rsquo;annuaire des entreprises &agrave; APIP-ANALYTICS afin que le vendredi prochain une pr&eacute;sentation de cet outil soit faites ;</p>', 22, 0),
(69, 'Chef service Code des Investissements', '<p>Il a fait le compte rendu de la s&eacute;ance de travail qu&rsquo;il a eue avec le conseiller Partenariat Publics-Priv&eacute;s de Monsieur le Premier Ministre dans le cadre du code des investissements.</p>', 22, 0),
(70, 'Le Directeur Général', '<ul>\r\n	<li>Il a inform&eacute; les directeurs du compte rendu qu&rsquo;il a fait de la rencontre avec la Direction G&eacute;n&eacute;rale de la Banque Nationale d&rsquo;Investissements de Guin&eacute;e (BNIG) relative au projet de 100.000 entrepreneurs&nbsp;;</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Il a demand&eacute; au service Communication et Marketing de faire un lancement du site ce jeudi puis de proc&eacute;der &agrave; un battage m&eacute;diatique dans le but de communiquer sur la disponibilit&eacute; de ce nouveau site et des changements qui y ont &eacute;t&eacute; apport&eacute;s &nbsp;;</li>\r\n	<li>Il a demand&eacute; &agrave; la directrice promotion des investissements de diversifier les projets qui doivent faire l&rsquo;objet d&rsquo;&eacute;tude ou de financement pendant le GUIF.</li>\r\n	<li>Il a demand&eacute; au service projets et partenariats de mettre un focus sur les financements qui aboutissent plus sur l&rsquo;investissement que sur les micro financements en adoptant une approche de segmentation et d&rsquo;inclusion.</li>\r\n</ul>', 23, 0),
(71, 'Directrice Général Adjointe', '<ul>\r\n	<li>Elle a pass&eacute; en revue les recommandations de la derni&egrave;re r&eacute;union de direction afin de savoir &agrave; quel niveau d&rsquo;ex&eacute;cution &eacute;taient ces derni&egrave;res en fonction des directions concern&eacute;es&nbsp;;</li>\r\n	<li>Elle a f&eacute;licit&eacute; la coordination projets et partenariats pour l&rsquo;&eacute;bauche du projet de structuration d&rsquo;un partenariat avec les banques qu&rsquo;elle compte mettre en &oelig;uvre. Elle a demand&eacute; &agrave; cette &eacute;quipe de faire en sorte que tous les aspects de financements soient pris en compte.</li>\r\n</ul>', 23, 0),
(72, 'Directeur Amélioration Climat des Affaires', '<p>Il a fait le compte rendu des activit&eacute;s de la pr&eacute;paration de la campagne de communication sur la r&eacute;servation de nom en ligne en collaboration avec IFC</p>', 23, 1),
(73, 'Directeur Amélioration Climat des Affaires', '<p>Il a fait le compte rendu des activit&eacute;s de la pr&eacute;paration de la campagne de communication sur la r&eacute;servation de nom en ligne en collaboration avec IFC ;</p>', 23, 0),
(74, 'Directrice Promotion des Investissements', '<ul>\r\n	<li>&nbsp;Elle a inform&eacute; la Direction G&eacute;n&eacute;rale des &eacute;changes et travaux pr&eacute;paratoires relatifs au lancement officiel du GUIF ce vendredi 11 d&eacute;cembre 2020 ;</li>\r\n	<li>Elle a fait la pr&eacute;sentation du site internet du Guinea Investments Forum (GUIF)&nbsp;;</li>\r\n	<li>Elle a inform&eacute; la Direction G&eacute;n&eacute;rale de la confirmation d&rsquo;un cabinet qui dans le cadre du GUIF est pr&ecirc;t &agrave; payer 1500 usd pour avoir acc&egrave;s a la plateforme des projets.</li>\r\n</ul>', 23, 0),
(75, 'Chargé de projets', '<p>Il a fait une pr&eacute;sentation relative &agrave; la nouvelle approche que le service Projets et Partenariats&nbsp;compte adopter pour permettre aux entrepreneurs d&rsquo;avoir plus d&rsquo;acc&egrave;s aux financements aupr&egrave;s des banques dans le cadre du projet de structuration d&rsquo;un partenariat avec les banques ;</p>', 23, 0),
(76, 'Coordinateur projet et partenariats', '<p>Il a donn&eacute; des explications sur le concept de mise en place d&rsquo;un hub bancaire que son service murit dans le cadre de la mise en place du projet de structuration d&rsquo;un partenariat avec les banques.</p>', 23, 0),
(77, 'Directeur Etudes, Statistiques et Innovation', '<ul>\r\n	<li>Il a inform&eacute; la Direction G&eacute;n&eacute;rale qu&rsquo;il a contact&eacute; MTN et orange dans le cadre de l&rsquo;utilisation des wifi pocket conform&eacute;ment aux recommandations de la derni&egrave;re r&eacute;union de direction&nbsp;;</li>\r\n	<li>Il a inform&eacute; la Direction G&eacute;n&eacute;rale de la disponibilite des sites du GUIF et de l&rsquo;APIP d&egrave;s demain 9 d&eacute;cembre 2020.</li>\r\n</ul>', 23, 0),
(78, 'Directrice Appui aux Entreprises', '<p>Elle a fait le point sur les activit&eacute;s en cours au sein de sa direction.</p>', 23, 0),
(79, 'Georges Lamah', '<p>Regard des d&eacute;couvertes</p>', 25, 0),
